(46,	'woocommerce-shipping-fedex-api-outage-2023-07-16',	'warning',	'en_US',	'Scheduled FedEx API outage — July 2023',	'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(47,	'wcship-2023-07-hazmat-update-needed',	'update',	'en_US',	'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension',	'<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(48,	'woocommerce-WCStripe-Aug-2023-update-needed',	'update',	'en_US',	'Action required: Security update for WooCommerce Stripe plugin',	'<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(49,	'woocommerce-WCStripe-Aug-2023-security-updated',	'update',	'en_US',	'Security update of WooCommerce Stripe plugin',	'<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(50,	'woocommerce-WooPayments-Aug-2023-security-updated',	'update',	'en_US',	'Security update of WooPayments (WooCommerce Payments) plugin',	'<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(51,	'avalara_q3-2023_noAvaTax',	'marketing',	'en_US',	'Automatically calculate VAT in real time',	'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(52,	'woocommerce-usermeta-Sept2023-productvendors',	'update',	'en_US',	'Your store requires a security update',	'<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(53,	'woocommerce-STRIPE-Oct-2023-update-needed',	'update',	'en_US',	'Action required: Security update for WooCommerce Stripe Gateway',	'<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(54,	'amazon-mcf-reviews-2023-12-07',	'marketing',	'en_US',	'Enjoying Amazon MCF for WooCommerce?',	'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(55,	'remove_estimated_deposits_2024',	'marketing',	'en_US',	'Estimated deposits are going away',	'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(56,	'sirt-woo-2024-11-xss-admin',	'update',	'en_US',	'An important update is required for WooCommerce',	'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(57,	'woocommerce-analytics-order-attribution-promotions-december-2024',	'marketing',	'en_US',	'Discover what drives your sales',	'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.',	'{}',	'unactioned',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(58,	'paypal-braintree-sdk-depreciation-2025-02-20',	'update',	'en_US',	'Update required for your PayPal Braintree extension',	'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(59,	'viva_wallet_migration_q1_2025',	'update',	'en_US',	'Switch to the new Viva.com Smart Checkout',	'The Viva Wallet extension is no longer being updated and improved. To get the best Viva.com experience, move to the new Smart Checkout. It’s fast, secure, and fully compatible with the latest WooCommerce features.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(60,	'stripe_orderstatus_bug_q2_2025',	'update',	'en_US',	'Action Required: Update Order Status Manager',	'Update to the latest Order Status Manager (v1.15.5+) to ensure payments are captured. Your version of WooCommerce Order Status Manager can interfere with order processing for payment gateways (including WooCommerce Stripe Gateway v9.2.0+), which may cause orders to process without capturing payment.',	'{}',	'pending',	'woocommerce.com',	'2025-04-06 00:04:16',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(61,	'wc-admin-add-first-product-note',	'email',	'en_US',	'Add your first product',	'{greetings}<br /><br />Nice one; you\'ve created a WooCommerce store! Now it\'s time to add your first product and get ready to start selling.<br /><br />There are three ways to add your products: you can <strong>create products manually, import them at once via CSV file</strong>, or <strong>migrate them from another service</strong>.<br /><br /><a href=\"https://woocommerce.com/document/managing-products/?utm_source=help_panel&amp;utm_medium=product\">Explore our docs</a> for more information, or just get started!',	'{\"role\":\"administrator\"}',	'unactioned',	'woocommerce-admin',	'2025-04-08 00:51:46',	NULL,	0,	'plain',	'http://project.local/wp-content/plugins/woocommerce/images/admin_notes/dashboard-widget-setup.png',	0,	0,	'info'),
(63,	'wc-admin-mobile-app',	'info',	'en_US',	'Install Woo mobile app',	'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.',	'{}',	'unactioned',	'woocommerce-admin',	'2025-04-08 00:51:46',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(64,	'wc-admin-launch-checklist',	'info',	'en_US',	'Ready to launch your store?',	'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.',	'{}',	'unactioned',	'woocommerce-admin',	'2025-04-09 20:13:44',	NULL,	0,	'plain',	'',	0,	0,	'info'),
